mapper.select:
SELECT SEQ ,GROUP_CODE ,(SELECT REG_DESC FROM USER_GROUP WHERE GROUP_CODE = UD.GROUP_CODE) AS GROUP_NM ,NAME ,USER_ID ,DATE_FORMAT(REG_DATE,'%Y-%m-%d') AS REG_DATE ,VALIDATE_DATE ,REG_DESC FROM USER_DETAIL UD WHERE UD.DEL_DATE IS NULL
AND UD.USER_ID LIKE CONCAT('%',#{keyWord},'%')
AND UD.NAME LIKE CONCAT('%',#{keyWord},'%')
AND UD.GROUP_CODE = #{groupCode}
ORDER BY UD.SEQ DESC LIMIT #{limitStart}, #{limitEnd}

mapper.update:
UPDATE USER_DETAIL
	SET DEL_USER_ID = #{delUserId}
	,DEL_DATE = NOW()
WHERE
	SEQ = #{seq}
	AND USER_ID = #{userId}


UPDATE USER_DETAIL SET
		DEPARTMENT = #{department}

		,GROUP_CODE = #{groupCode}


		,NAME = #{name}


		,USER_PWD = password(#{userPwd})


		,REG_DESC = #{regDesc}


		,VALIDATE_DATE = #{validateDate}

WHERE SEQ = #{seq}
AND USER_ID = #{userId}
