mapper.select:
SELECT COUNT(*) AS TOTCNT FROM STATUS WHERE 1=1
AND STATUS = #{status}
AND NAME LIKE CONCAT('%',#{keyWord},'%')

SELECT ST.NO ,ST.NAME ,ST.STATUS ,(SELECT DESCRIPTION FROM STATUS_GROUP WHERE STATUSNO = ST.STATUS) AS DESCRIPTION ,MCI.POL_DISTRICT ,MCI.ROUTE_CD ,MCI.SEQ AS CROSSROAD_SEQ ,CASE WHEN MCI.SEQ IS NULL THEN 'N' ELSE 'Y' END AS LINKED_YN ,MCI.LAT ,MCI.LNG FROM STATUS ST LEFT OUTER JOIN DW_ITSWEB.MGMT_CROSSROAD_INFO MCI ON ST.NO = MCI.LINKED_SEQ WHERE 1=1
AND ST.STATUS = #{status}
AND ST.NAME LIKE CONCAT('%',#{keyWord},'%')
ORDER BY ST.NO DESC LIMIT #{limitStart}, #{limitEnd}

mapper.insert:

	SELECT IFNULL(MAX(NO),0) + 1 AS NO FROM STATUS

INSERT INTO STATUS (
	NO
	,NAME
	,STATUS
)VALUES(
	#{no}
	,#{name}
	,#{status}
)
