mapper.select:
SELECT COUNT(MCI.SEQ) OVER() AS TOT_CNT ,MCI.SEQ ,MCI.POL_CD ,MCI.POL_DISTRICT ,MCI.ROUTE_CD ,MCI.CROSSROAD_NM ,MCI.ROAD_NM ,MCI.LAT ,MCI.LNG ,MCI.LINKED_SEQ FROM MGMT_CROSSROAD_INFO MCI WHERE MCI.DEL_DATE IS NULL
AND MCI.POL_CD = #{polCd}
AND MCI.POL_DISTRICT = #{polDistrict}
AND MCI.ROUTE_CD = #{routeCd}
ORDER BY MCI.POL_DISTRICT ASC ,MCI.SEQ ASC

SELECT
	COUNT(*) AS CNT
FROM MGMT_CROSSROAD_INFO
WHERE DEL_DATE IS NULL
AND POL_DISTRICT = #{polDistrict}
AND ROUTE_CD = #{routeCd}
AND CROSSROAD_NM = #{crossroadNm}

SELECT 
	COUNT(*) AS TOT_CNT
FROM MGMT_CROSSROAD_INFO MCI
WHERE MCI.DEL_DATE IS NULL

	AND MCI.POL_CD = #{polCd}


	AND MCI.POL_DISTRICT = #{polDistrict}


	AND MCI.ROUTE_CD = #{routeCd}


	AND MCI.CROSSROAD_NM LIKE CONCAT('%',#{crossroadNm},'%')

ORDER BY MCI.CROSSROAD_NM ASC

SELECT MCI.SEQ ,MCI.POL_CD ,MCI.POL_DISTRICT ,MCI.ROUTE_CD ,FN_GET_ROUTE_NM(MCI.ROUTE_CD) AS ROUTE_NM ,MCI.CROSSROAD_NM ,MCI.ROAD_NM ,MCI.LAT ,MCI.LNG ,MCI.LINKED_SEQ
FROM MGMT_CROSSROAD_INFO MCI WHERE MCI.DEL_DATE IS NULL
AND MCI.POL_CD = #{polCd}
AND MCI.POL_DISTRICT = #{polDistrict}
AND MCI.ROUTE_CD = #{routeCd}
AND MCI.CROSSROAD_NM LIKE CONCAT('%',#{crossroadNm},'%')
ORDER BY MCI.POL_DISTRICT ASC ,MCI.SEQ ASC LIMIT #{limitStart}, #{limitEnd}

mapper.insert:
INSERT INTO MGMT_CROSSROAD_INFO (
	POL_CD
	,POL_DISTRICT
	,ROUTE_CD
	,CROSSROAD_NM
	,ROAD_NM

	,LAT


	,LNG


	,LINKED_SEQ

	,REG_USERID
	,REG_DATE
) VALUES (
	#{polCd}
	,#{polDistrict}
	,#{routeCd}
	,#{crossroadNm}
	,NULL

	,#{lat, jdbcType=DECIMAL}


	,#{lng, jdbcType=DECIMAL}


	,#{linkedSeq, jdbcType=VARCHAR}

	,#{regUserid}
	,NOW()
)

mapper.update:
UPDATE MGMT_CROSSROAD_INFO SET
	UPDT_DATE = NOW()
	,UPDT_USERID = #{updtUserid}

	,POL_CD = #{polCd}


	,POL_DISTRICT = #{polDistrict}


	,ROUTE_CD = #{routeCd}


	,CROSSROAD_NM = #{crossroadNm}


	,ROAD_NM = #{roadNm}


	,LAT = #{lat, jdbcType=VARCHAR}


	,LNG = #{lng, jdbcType=VARCHAR}


	,LINKED_SEQ = #{linkedSeq}

WHERE SEQ = #{seq}

UPDATE MGMT_CROSSROAD_INFO
	SET DEL_USERID = #{delUserid}
		,DEL_DATE = NOW()
WHERE SEQ = #{seq}

UPDATE MGMT_CROSSROAD_INFO
	SET DEL_USERID = #{delUserid}
		,DEL_DATE = NOW()
WHERE ROUTE_CD = #{routeCd}
