mapper.update:
UPDATE MGMT_CROSSROAD_INFO
	SET LAT = #{lat}
	,LNG = #{lng}
WHERE SEQ = #{seq}

mapper.select:
SELECT MAX(LAT) OVER() AS MAX_LAT ,MAX(LNG) OVER() AS MAX_LNG ,MIN(LAT) OVER() AS MIN_LAT ,MIN(LNG) OVER() AS MIN_LNG ,MCI.SEQ ,MCI.POL_CD ,MCI.POL_DISTRICT ,MCI.ROUTE_CD ,MCI.CROSSROAD_NM ,MCI.ROAD_NM ,MCI.LAT ,MCI.LNG ,MCI.LINKED_SEQ FROM MGMT_CROSSROAD_INFO MCI WHERE MCI.DEL_DATE IS NULL
AND MCI.POL_DISTRICT = #{polDistrict}
AND MCI.ROUTE_CD = #{routeCd}
AND MCI.CROSSROAD_NM LIKE CONCAT('%',#{keyWord},'%')
ORDER BY IFNULL(MCI.LAT,0) ASC, IFNULL(MCI.LNG,0) ASC, MCI.SEQ ASC

mapper.insert:
INSERT INTO MGMT_CROSSROAD_INFO (
	POL_CD
	,POL_DISTRICT
	,ROUTE_CD
	,CROSSROAD_NM
	,ROAD_NM

	,LAT


	,LNG


	,LINKED_SEQ

	,REG_USERID
	,REG_DATE
) VALUES (
	#{polCd}
	,#{polDistrict}
	,#{routeCd}
	,#{crossroadNm}
	,NULL

	,#{lat, jdbcType=DECIMAL}


	,#{lng, jdbcType=DECIMAL}


	,#{linkedSeq, jdbcType=VARCHAR}

	,#{regUserid}
	,NOW()
)
